<?php /*	分页类*/class Page{    public $count; //数据总条数,通过model类中的getNum获取	public $totalPage; //总页数	public $now; //当前页	public $prev; //上一页	public $next;//下一页	public $offset;//偏移量，每页的数目	public $start; //开始的位置		public function __construct($now,$count,$offset=5)	{		$this->now = $now;		$this->count = $count;		$this->offset = $offset;				$this->getTotalPage();		$this->getPrev();		$this->getNext();		$this->getStart();			}	//获取总页数	protected function getTotalPage()	{		$this->totalPage = ceil($this->count/$this->offset);	}    //获取上一页    protected function getPrev()	{	    If($this->now == 1){			$this->prev = 1;		} else {			$this->prev = $this->now - 1;		}	}    	 //获取下一页    protected function getNext()	{	    If($this->now == $this->totalPage) {			$this->next = $this->totalPage;		} else {			$this->next = $this->now + 1;		}	}		 //获取下一页    protected function getStart()	{	    $this->start = ($this->now -1) * $this->offset;	}	}//$page = new Page();//$page->getTotalPage();//$page->getNext();//echo $page->next;